package bsmodule

import "fmt"

type Module struct {
	Code int
	Name string
}

func (m Module) String() string {
	return fmt.Sprintf("%s", m.Name)
}

func NewModule(code int, name string) Module {
	return Module{
		Code: code,
		Name: name,
	}
}

